            function registerOfferClickEvents(){
                fn_class = '';
                
                $(".offer-reward").click(function(e){

                    window.shouldOpenNotifyPopup = false;

                    fn_offerId = $(this).data("offer_id");
                    fn_targetUrl = $(this).data("target_url"); 
                    fn_is_ios = $(this).data('is_ios');
