            function toggleLoader(is_show, modal, target_loader){
                selector = $(modal);
                selector.find("div.modal-body > div").css("display", "none");
                if(is_show){
                    selector.find("div.modal-content").css({"background":"transparent", "border":"none"});
                    selector.find("div.modal-header").hide();
                    selector.find("div.modal-footer").hide();
                    $(target_loader).show();
                    selector.find("div.modal-body").addClass("text-center");
                }else{
                    selector.find("div.modal-content").css({"background": "","border":"1px solid rgba(0,0,0,.2)"});
                    selector.find("div.modal-header").show();
                    selector.find("div.modal-footer").show();
                    $(target_loader).hide();
                    selector.find("div.modal-body").removeClass("text-center");
                }   
            }

            function openOfferModal(){
                if( !window.shouldOpenNotifyPopup ){
                    $("#mW2m .modal-title").empty().html(
                        fn_oname + ' - ' + fn_opay + ' ' + currency_name
                    );
                                            if(fn_class == 'mobile_link'){ 
                            $("#mW2m").find(".modal-dialog").removeClass('modal-lg');
                            $("#mW2m_offer_desc").css("font-size", "18px");
                        }else{ 
                            $("#mW2m").find(".modal-dialog").addClass('modal-lg');
                            $("#mW2m_offer_desc").css("font-size", "15px");
                        }
                                    }else {
                    $("#mW2m .modal-title").empty().html(
                        '<i class="fas fa-info-circle"></i> Tracking - iOS 14 Notification'
                    );
                }

                $("#mW2m").modal('toggle');

                $("#web2m_header").show();
                $("#mW2m_offer_img").prop('src', fn_offer_img);
                $("#mW2m_offer_desc").empty().html(fn_offer_desc);
                
                $("#content_popup_web2mobile").hide();
                $("#content_popup_error").hide();
                $("#non_web2m_info").hide();
                $("#web2m_info").hide();
                $("#clickpopup_info").hide();
                $("#ios14_notif").hide();
                $("#send_info").hide();

                toggleLoader(true,
                    $("#mW2m"),
                    $("#mW2m_loader")
                );

                if(fn_class=='fancybox_mobile'){
                    $.post(fn_targetUrl,function(res){

                        toggleLoader(false,
                            $("#mW2m"),
                            $("#mW2m_loader")
                        );

                        if( res.version != undefined ){
                                if( res.offer_action != "" )
                                    res.offer_action = "<li>" + res.offer_action + "</li>";

                                res.offer_img = res.offer_img.replace('http://', '//').replace('https://', '//');

                                $(".mobDevice_Content").text(res.mobile_device);
                                $(".earnlabel").text("Earn " + fn_opay + ' ' + currency_name);
                                $(".how-to-earn-instructions-addtl").empty().html(res.offer_action);

                                window.mobile_enc = res.mobile_enc;
                                window.subid1 = res.subid1;
                                window.subid2 = res.subid2;
                                window.subid3 = res.subid3;
                                window.web2m_offerid = res.offer_id;
                                window.qr_tracking_link = res.qr_tracking_link;

                                generateQRCode(window.qr_tracking_link);

                                $("#content_popup_web2mobile").show();
                                $("#web2m_info").show();

                                $('.send-options a').first().click(); // reset link position

                        }else{ // show error
                                $("#content_popup_error").show();
                                $("#content_popup_error_message").text(res);
                        }  

                    });
                                                }else{
                    toggleLoader(false,
                        $("#mW2m"),
                        $("#mW2m_loader")
                    );
                    $("#content_popup_web2mobile").show();
                    $("#non_web2m_info").show();
                }

            }

            function registerTrEvent(){
                $(".offert").on('click', function (event) {
                    if ( !$(event.target).is('.offer-reward') ) 
                        $(this).find('.offer-reward').trigger('click');
                });
            }

            function web2mEvents(){

                    $('#content_popup_web2mobile .send-options').on('click', 'a', function(e){
                        e.preventDefault();

                        toggle_active_menu(this);
                        toggle_send_options(this);
                    });

                    $('#send_email_form').on('submit', function(e) {
                        e.preventDefault();

                        var email1 = $('input[name=email1]').val();
                        var email2 = $('input[name=email2]').val();
                        var agree = $('#agree').is(":checked") ? 'yes' : 'no';
                        var recaptcha_response_field = $('#recaptcha_response_field').val();

                        if(recaptcha_response_field === '') {
                            set_error('Captcha is invalid');
                            window.grecaptcha.reset();
                            return false;
                        }

                        if( $("#agree").is(':checked') === false ){
                            set_error('You need to agree with our terms of service.', $('.agreetext'), {'color':'red', 'font-style':'italic'}, {'color':'black', 'font-style':'normal'});
                            return false;
                        }

                        //subids
                        var subids_str = '';
                        if(subid1 !== ''){
                            subids_str = subid1 + '/';
                        }
                        if(subid2 !== ''){
                            subids_str += subid2 + '/';
                        }
                        if(subid3 !== ''){
                            subids_str += subid3 + '/';
                        }

                        $.post(
                            '/ajax/mobile_offer_email/'+window.web2m_offerid+'/'+window.s2+'/' + subids_str, {
                                wEmail: email1,
                                wEmail_re: email2,
                                wEnc: window.mobile_enc,
                                agree: agree,
                                recaptcha_response: recaptcha_response_field
                            }, function(d) {
                                var t = $.parseJSON(d);
                                
                                $("#send_info").show();
                                if(t.result !== 'ok') {
                                    set_error(t.message);
                                    
                                    $("#send_info_text").text("Error! " + t.message);    
                                    return false;
                                }
                                set_success('Sent link to your email. Please open your inbox in your phone and access the link.');
                                
                                $("#send_info_text").text("Success! An email was sent to " + email1 + ".");

                                $('#send_email_form')[0].reset();
                                window.grecaptcha.reset();
                            }
                        );

                    });

                    toggle_active_menu('.send-options a[rel=qr]');
                    toggle_send_options('.send-options a[rel=qr]');

                    function set_error(msg, extra_element = '', extra_css = '', normal_css = '') {
                        $('#error_msg .msg').text(msg);
                        $('#error_msg').show();
                        $("#submit_btn").hide();

                        if(extra_element != '' && extra_css != '')
                            extra_element.css(extra_css);
                        
                        var to = setTimeout(function(){
                            $('#error_msg .msg').text('');
                            $('#error_msg').hide();
                            $("#submit_btn").show();
                            
                            if(extra_element != '' && normal_css != '')
                                extra_element.css(normal_css);
                        }, 3000);

                    }

                    function set_success(msg) {
                        $('#success_msg .msg').text(msg);
                        $('#success_msg').show();
                        $("#submit_btn").hide();
                        var to = setTimeout(function(){
                            $('#success_msg .msg').text('');
                            $('#success_msg').hide();
                            $("#submit_btn").show();
                        }, 3000);

                    }

                    function toggle_active_menu(el) {
                        $('.send-options a').removeClass('active');
                        $(el).addClass('active');
                    }
                    /**
                    *
                    * @param value
                    */
                    function toggle_send_options(el) {
                        var target_alias = $(el).attr('rel');
                        var target_id = 'send_option_email';
                        if(target_alias === 'qr') {
                            target_id = 'send_option_qr';
                        }

                        $('.target-option').hide();
                        $('#' + target_id).show();
                    }
            }

            function registerMissingEarningsEvent(){
                currency = window.currency_name_for_el;
                should_reload_my_earnings = false;
                $(".tabs-head").on('click', '#offers-'+currency+'-tab', function(){
                    let data = {
                        pub_id: 27435,
                        app_id: 11846,
                        user_id: window.userid
                    };

                    toggleEarningsLoader(true);

                    $.post("/my_earnings/show", data, function(res){

                        toggleEarningsLoader(false);

                        if(res == '0a'){
                            $("#error_config_message").text("Credentials");
                            $("#error_config").show();
                        }else if(res == '0b'){
                            $("#error_config_message").text("Configuration");
                            $("#error_config").show();
                        }else{
                            if( res.length === 0 ){
                                $("#no_earnings").show();
                            }else{
                                res.forEach(el => {
                                    if( el.is_show_missing == '1' )
                                        el.mc_url = '<br> <a href="#" data-target_link="'+el.mc_url+'" onclick="get_my_missing_credits(this);return false;">Missing Credits?</a>';
                                    else el.mc_url = '';
                                });

                                myEarnings =res.map(Earnings).join('');
                                $('#earnings_table tbody').empty().html(myEarnings);
                                $('#earnings_table').show();
                            }
                        }
                    });
                });

                function toggleEarningsLoader(is_show_loader){
                    $("#earnings-container").show();
                    $("#earnings-missing").hide();

                    $("#earnings_table").hide();
                    $("#error_config").hide();
                    $("#no_earnings").hide();

                    if(is_show_loader)
                        $(".earnings_loader").show();
                    else
                        $(".earnings_loader").hide();
                }

                $("#missing_credits_form").on('submit', function(e){
                    e.preventDefault();
                    url = $(this).prop("action");

                    $("#mMissingCredits").modal('toggle');

                    toggleLoader(true, 
                        $("#mMissingCredits"),
                        $("#mMissingCredits_loader")
                    );

                    $.ajax({
                        type: 'POST',
                        url: url,
                        data: new FormData( $("#missing_credits_form")[0] ),
                        processData: false, 
                        contentType: false, 
                        success: function(res) {
                            res = JSON.parse(res);
                            toggleLoader(false, 
                                $("#mMissingCredits"),
                                $("#mMissingCredits_loader")
                            );
                            $("#mMissingCredits_body").show();

                            $("#div_additional_data").empty().hide();

                            if( res.error == '0' ){ // success message
                                if( res.additional_data != 'undefined' )
                                    $("#div_additional_data").empty().html(res.additional_data).show();
                                
                                $("#missingCredits_success").show();
                                $("#mMissingCredits").modal('show');
                                should_reload_my_earnings = true;

                            }else{
                                $("#mMissingCredits_error p").html(res.data);
                                $("#mMissingCredits_error").show();

                                $("#mMissingCredits").modal('show');    
                            }

                            $("#trigger_cancel_missing_earnings").click();
                        }
                    });
                });

                $("#trigger_submit").on('click', function(e){
                    // check if screenshots were uploaded
                    values = $("input[name='image[]']").map(function(){ if($(this).val()) return $(this).val();}).get();
                    if(values.length > 0) $("input[name='image[]']").removeProp("required");
                    else $("input[name='image[]']").prop("required");
                });

                $("#trigger_cancel_missing_earnings").on('click', function(e){
                    e.preventDefault();
                    $("#missing_credits_form").prop("action", ""); // clear action
                    $("#missing_credits_form")[0].reset();
                    
                    $("#earnings-missing").hide();
                    if(should_reload_my_earnings){
                        $('#offers-'+currency+'-tab').click();
                    }else $("#earnings-container").show();
                    
                });

                $(".rad_confirm_questions").on('change', function(e){
                    if(
                        $("input[name=rad_ft]:checked").val() == 'yes' &&
                        $("input[name=rad_legit]:checked").val() == 'yes' &&
                        $("input[name=rad_completed]:checked").val() == 'no'
                    ) $("#trigger_submit").removeProp("disabled");
                    else $("#trigger_submit").prop("disabled", true);
                });
            }

            function get_my_missing_credits(e){
                
                target_href = $(e).data('target_link');

                toggleLoader(true, 
                    $("#mMissingCredits"),
                    $("#mMissingCredits_loader")
                );

                $.get(target_href, function(res){

                    res = JSON.parse(res);

                    toggleLoader(false, 
                        $("#mMissingCredits"),
                        $("#mMissingCredits_loader")
                    );
                    $("#mMissingCredits_body").show();
                    $("#missingCredits_success").hide();
                    $("#missingCredits_error").hide();
                    $("#div_additional_data").empty().hide();

                    if(res.error == '1'){ // do not show modal
                        $("#earnings-container").hide();
                        $("#earnings-missing").show();
                        $("#mMissingCredits").modal('hide');

                        $("#em-offername").empty().text(res.data.offer.name);
                        $("#missing_credits_form").prop("action", res.data.url);

                    }else if( res.error == '0' ){ // success message
                        $("#missingCredits_success").show();
                        $("#mMissingCredits").modal('show');

                    }else if(res.error == '0b1'){
                        $("#mMissingCredits_error p").html('You need to wait for ' + res.remaining_h + ' hour(s) and '+ res.remaining_m + ' min(s) before claiming this offer');
                        $("#mMissingCredits_error").show();

                        $("#mMissingCredits").modal('show');
                    }else if(res.error == '0b3'){
                        $("#mMissingCredits_error p").html("Sorry, you can only complete up to 3 missing "+res.data.currency_name+" requests per day.");
                        $("#mMissingCredits_error").show();
                        $("#mMissingCredits").modal('show');   
                    }else if(res.error == '0b4'){
                        $("#mMissingCredits_error p").html(res.data);
                        $("#mMissingCredits_error").show();
                        $("#mMissingCredits").modal('show');   
                    }else { // still has error
                        $("#mMissingCredits_error p").html(res.data);
                        $("#mMissingCredits_error").show();

                        $("#mMissingCredits").modal('show');
                    }
                    
                });
                
                return false;
            }

            function recaptcha_callback(response) {
                $('#recaptcha_response_field').val(response);
            }

            function generateQRCode(url) {
                $('#qrCodeCanvas').empty().qrcode({
                    text: url,
                    width: 240,
                    height: 240
                });
            }

            function sortEvents(){
                $(".tabs-head a.nav-link").on('click', function(){
                    el = $(this).data("elname");
                    $('.tabs-sort li').hide();
                    
                    tc = $(this).data("tcode");
                    if(tc != "" && tc != 'undefined')
                        $.get("/ifr/track/"+tc+"/11846");
                    
                                        $('.tabs-sort .nav-dropdown-'+el).show();
                    if(el == 'mobile_apps'){
                        $(".nav-mobile_apps").show();
                        $(".nav-link-mobile_apps").eq(0).click();
                    }
                    
                    scrollTo();
                });

                                    $(".tabs-sort").on('click', 'a.dropdown-item', function(e){
                        e.preventDefault();

                        sortParent = $(this).data('sortname');
                        sortWhat = $(this).data('sort');

                        if(sortWhat == 'fr') generalSort(sortParent, 'final_rating', true);
                        else sortByType(sortParent, sortWhat);
                    });
                
                function sortByType(sortParent, sortWhat){
                    let asc = false;

                    sortingAs = "offer_payout"; ptext = ".sortp_text";
                    textDesc = "High to Low"; textAsc = "Low to High";
                    if(sortWhat == 'age'){
                        sortingAs = "date_updated"; ptext = ".sorta_text";
                        textDesc = "New to Old"; textAsc = "Old to New";
                    }

                                            let sortTextElem = $(".nav-dropdown-"+sortParent).find(ptext);
                    
                    if( sortTextElem.text() == textDesc ){
                        asc = true;
                        sortTextElem.text(textAsc);
                        
                        if(sortWhat == 'payout')
                            $(".nav-sort-"+sortParent+" a.nav-link").data('srpayout', '0').attr('data-srpayout', '0');
                        else if(sortWhat == 'age')
                            $(".nav-sort-"+sortParent+" a.nav-link").data('srage', '0').attr('data-srage', '0');

                    }else {
                        sortTextElem.text(textDesc);

                        if(sortWhat == 'payout')
                            $(".nav-sort-"+sortParent+" a.nav-link").data('srpayout', '1').attr('data-srpayout', '1');
                        else if(sortWhat == 'age')
                            $(".nav-sort-"+sortParent+" a.nav-link").data('srage', '1').attr('data-srage', '1');
                    }

                    generalSort(sortParent, sortingAs, asc);
                }

                function generalSort(sortParent, sortWhat = '', asc = false){
                    var tbody = $(".tabs-body #offers-"+sortParent).find('tbody');
                    tbody.find('tr').sort(function(a, b) {
                        let A = $(a).data(sortWhat);
                        let B = $(b).data(sortWhat);

                        if (!isNaN(A)) A = Number(A);
                        if (!isNaN(B)) B = Number(B);

                        return asc == true ? B - A : A - B;

                    }).appendTo(tbody);
                }

                                    $(".tabs-sort").on('click', 'a.nav-link-mobile_apps', function(e){
                        e.preventDefault();
                        $(".nav-link-mobile_apps").removeClass('active');
                        $(this).addClass('active');

                        el = $(this).data("elname");
                        device = $(this).data("filter");

                        tr = $(".tabs-body #offers-"+el+" table").find('tr');
                        if (device == '' || device == 'all') {
                            tr.show();
                            scrollTo();
                            return;
                        }

                        tr.hide();

                        tr.filter(function (i, v) {
                            if(typeof $(v).data('offer_device') == 'undefined')
                                return true;
                            
                            if( $(v).data('offer_device').indexOf(device) >= 0 )
                                return true;
                            return false;
                        }).show();

                        scrollTo();
                    });
                            }
        
