            if( getComputedStyle(document.getElementById("detect"))["display"] == "none" ){
                $("#mNotifyBlock").modal('toggle');
                $("#notify_blocker_inst").show();
                is_blocker_enabled = true;
            }

            createTabs();

                            registerTrEvent();

                
                                showNotifyIfComputer();
                
                registerOfferClickEvents();

                web2mEvents();

                registerMissingEarningsEvent();

                sortEvents();
                            
        });
    </script>
